{{(function () {
  let config = {
			type: "msline",
			dataSource: {
				chart: {
					caption: "Metric actions across PRs",
					subCaption: "Showing selected actions across PRs for the metric - other",
					xAxisName: "Meta ID",
					theme: "fusion"
    		},
				categories: [{category: []}],
				dataset: [],
				trendlines: [{
					line: []
				}]
			}
		}
		let d =  run.data
		d.map((v) => {
				if(!_.find(config.dataSource.categories[0].category, {label: v.meta.toString()})) {
					config.dataSource.categories[0].category.push({"label": v.meta.toString()})
				}
				if(!_.find(config.dataSource.dataset, {seriesname: v.action})) {
					if(v.metric == 'other')
					 config.dataSource.dataset.push({seriesname: v.action, data: [{value: v.median}]})
				} else {
					if(v.metric == 'other')
					 _.find(config.dataSource.dataset, {seriesname: v.action}).data.push({value: v.median})
				}
		})

	config.dataSource.dataset.map((v) => {
			var x = _.meanBy(v.data, (p) => p.value)
			config.dataSource.trendlines[0].line.push({
				"startvalue": x,
				"color": Math.floor(Math.random()*16777215).toString(16),
				"valueOnRight": "1",
				"displayvalue": "Mean"
			})
		})

		return config
})();}}

