actionConfiguration.body:
SELECT run_meta.pull_request_id, run_meta.created_at, machine, metric, action, meta, (array_agg(run_meta.is_anomaly))[1] as is_anomaly, (array_agg(run_meta.is_merge_commit))[1] as is_merge_commit, PERCENTILE_CONT(0.5) WITHIN GROUP(ORDER BY value) as median from public.run
LEFT JOIN run_meta ON run.meta = run_meta.id
WHERE metric = '{{Select1.selectedOptionValue}}' {{SelectMachineFilter.selectedOptionValue ? "AND machine = '" + SelectMachineFilter.selectedOptionValue + "'": ""}}  AND action IN ({{MultiSelect2.selectedOptionValues.reduce((result, item, index) => {
		return result + "'" + item + "'" + (index != MultiSelect2.selectedOptionValues.length - 1 ? "," : "")
	}, "")}}) {{MergeCommitSwitch.isSwitchedOn ? " AND is_merge_commit " : ""}} AND run_meta.created_at >= '{{DatePicker1.selectedDate}}' AND pull_request_id != -1 AND run_meta.created_at <= '{{DatePicker2.selectedDate}}' GROUP BY metric, action, meta, machine, run_meta.pull_request_id, run_meta.created_at ORDER BY run_meta.created_at

